import styled from 'styled-components'
import { Variables } from '../styled.var'

export const Header = styled.header`
    display: flex;
    width: 100vw;
    height: 45px;
    justify-content: space-around;
    align-items: center;
    img{
        width: 85px;
        height: 18px;
    }
    button{
        padding: 70px;
        height: 22px;
        padding: 20px;
        border:${Variables.Pink} 1px solid;
        display: flex;
        align-items: center;
        background-color: white;

    }


`
