import styled from 'styled-components'
import { Variables as V } from '../../styled.var'

export const Container = styled.div`
    width: 45vw;
    height: 130px;
    margin-bottom: 60px;
    margin: 0 auto 60px auto;
    img{
        width: 30px;
        height: 25px;
    }
    h1{
        margin: 0;
        color: #00252E;
        font-size: 42px;
    }
`
