import styled from 'styled-components'
import { Variables as V } from '../../styled.var'

export const Footer = styled.footer`
    width: 100vw;
    height: 750px;
    background-color: #00252E;
    color: white;

    .content{
        margin: 20px;
    }
    h2{
        padding-top: 20px;
    }
    p{
        font-size: 12px;
    }
    input{
        width: 100%;
        height: 40px;
        border-radius: 10px;
        border: none;
    }
    button{
        width: 140px;
        height: 40px;
        background-color: ${V.LightPink};
    }

`
