import styled from 'styled-components'
import { Variables as V } from '../../styled.var'


export const Sec = styled.section`
    width: 80vw;
    margin: auto;
    text-align: center;

    p{
        margin: 0;
    }
    button{
        width: 70%;
        margin-top: 40px;
        padding: 15px;
        color: white;
        background-color: ${V.LightPink};
        font-family: ${V.Poppins};
        font-size: 10;
        border: 0;
        border-radius: 50px;
    }
`
